        <label class="text-lg font-semibold block">¿Qué animal es este?</label>
        <input type="text" v-model="respuestas.camello" @input="verificarRespuestas" class="w-28 px-2 py-1 rounded-lg border">
      </div>

    </div>

    <!-- Mostrar Puntuación en la Consola -->
    <div>
      <button @click="calcularPuntuacion" class="bg-blue-500 text-white px-4 py-2 rounded-md">Calcular Puntuación</button>
      <p class="text-lg font-semibold mt-4">Puntuación Total: {{ puntuacion }}</p>
    </div>
  </div>
</template>

<script>
import { onMounted } from 'vue';
import axios from 'axios';

export default {
  setup() {
    const ejecutarFuncion = () => {
      const text1 = "Escriba el nombre de estos animales en los inputs correspondientes comenzando de izquierda a derecha";
      const synthesis = window.speechSynthesis;
      const utterance = new SpeechSynthesisUtterance(text1);
      utterance.rate = 0.7; // Ajusta este valor para cambiar la velocidad
      synthesis.speak(utterance);
    };

    onMounted(() => {
      ejecutarFuncion();
    });

    return {
      ejecutarFuncion
    };
  },
  data() {
    return {
      respuestas: {
        leon: '',
        rinoceronte: '',
        camello: ''
      },
      puntuacion: 0
    };
  },

  methods: {
    verificarRespuestas() {
      // Puedes realizar validaciones adicionales aquí si lo deseas.
    },
    calcularPuntuacion() {
      const leonRespuesta = this.respuestas.leon.toLowerCase().normalize("NFD").replace(/[\u0300-\u036f]/g, "");
      const rinoceronteRespuesta = this.respuestas.rinoceronte.toLowerCase().normalize("NFD").replace(/[\u0300-\u036f]/g, "");
      const camelloRespuesta = this.respuestas.camello.toLowerCase().normalize("NFD").replace(/[\u0300-\u036f]/g, "");

      const answers= {
        identification_answer: leonRespuesta + ', ' + rinoceronteRespuesta + ', ' + camelloRespuesta 
      }

      let puntuacion = 0;
      if (leonRespuesta === "leon") {
        puntuacion++;
      }
      if (rinoceronteRespuesta === "rinoceronte") {
        puntuacion++;
      }
      if (camelloRespuesta === "camello" || camelloRespuesta === "dromedario") {
        puntuacion++;
      }

      //send to uploadIdentification '/moca/uploadIdentification'
      axios.post('/moca/uploadIdentification', answers)
      .catch(error => {
        console.log("error: ",error);
      });

      // Mostrar la puntuación en la consola
      console.log("Puntuación Total: " + puntuacion);

      this.puntuacion = puntuacion;
      this.$emit("answer-score", this.puntuacion);
    }
  }
};
</script>
